import 'package:flutter/material.dart';
import 'package:whatsappclone/models/chatmodel.dart';
import 'package:whatsappclone/pages/chatdetailscreen.dart';

class ChatTile extends StatelessWidget {
  ChatTile({Key? key, required this.chatdata}) : super(key: key);
  Chat chatdata;

  @override
  Widget build(BuildContext context) {
    return ListTile(
      onTap: () => Navigator.push(context, MaterialPageRoute(builder: (context) => chatDetails(chatdata: chatdata,),)),
      title: Text(chatdata.name!),
      leading: CircleAvatar(
        backgroundImage:(chatdata.avatar=="")? NetworkImage((chatdata.isGroup!)?"data:image/png;base64,":
            "data:image/png;base64,"):NetworkImage(chatdata.avatar!),
      ),
      subtitle: Text(chatdata.message!),
      trailing: Text(chatdata.updatedAt!),
    );
  }
}
